                      const emptyIndex = currentTasks.findIndex(t => !t.trim());
                      if (emptyIndex >= 0) {
                        const newTasks = [...currentTasks];
                        newTasks[emptyIndex] = randomTask;
                        setCurrentTasks(newTasks);
                      } else {
                        setCurrentTasks([...currentTasks, randomTask]);
