import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [clockInTime, setClockInTime] = useState('');
  const [clockOutTime, setClockOutTime] = useState('');
  const [timeBlocks, setTimeBlocks] = useState([]);
  const [currentDate, setCurrentDate] = useState(new Date().toISOString().split('T')[0]);
  const [shifts, setShifts] = useState([]);

  // Load saved shifts from localStorage on mount
  useEffect(() => {
    const savedShifts = localStorage.getItem('timeClockShifts');
    if (savedShifts) {
      setShifts(JSON.parse(savedShifts));
    }
  }, []);

  // Save shifts to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('timeClockShifts', JSON.stringify(shifts));
  }, [shifts]);

  const addTimeBlock = () => {
    setTimeBlocks([
      ...timeBlocks,
      {
        id: Date.now(),
        startTime: '',
        endTime: '',
        tasks: ''
      }
    ]);
  };

  const updateTimeBlock = (id, field, value) => {
    setTimeBlocks(
      timeBlocks.map(block =>
        block.id === id ? { ...block, [field]: value } : block
      )
    );
  };

  const removeTimeBlock = (id) => {
    setTimeBlocks(timeBlocks.filter(block => block.id !== id));
  };

  const calculateTotalHours = () => {
    if (!clockInTime || !clockOutTime) return 0;

    const clockIn = new Date(`2000-01-01T${clockInTime}`);
    const clockOut = new Date(`2000-01-01T${clockOutTime}`);
    const diffMs = clockOut - clockIn;
    const diffHours = diffMs / (1000 * 60 * 60);

    return diffHours > 0 ? diffHours.toFixed(2) : 0;
  };

  const saveShift = () => {
    if (!clockInTime || !clockOutTime) {
      alert('Please enter clock in and clock out times');
      return;
    }

    const newShift = {
      id: Date.now(),
      date: currentDate,
      clockInTime,
      clockOutTime,
      totalHours: calculateTotalHours(),
      timeBlocks: timeBlocks.filter(block => block.startTime && block.tasks)
    };

    setShifts([newShift, ...shifts]);

    // Reset form
    setClockInTime('');
    setClockOutTime('');
    setTimeBlocks([]);
  };

  const deleteShift = (id) => {
    if (window.confirm('Are you sure you want to delete this shift?')) {
      setShifts(shifts.filter(shift => shift.id !== id));
    }
  };

  const formatTime = (time) => {
    if (!time) return '';
    const [hours, minutes] = time.split(':');
    const hour = parseInt(hours);
    const ampm = hour >= 12 ? 'PM' : 'AM';
    const formattedHour = hour % 12 || 12;
    return `${formattedHour}:${minutes} ${ampm}`;
  };

  const formatDate = (dateStr) => {
    const date = new Date(dateStr + 'T00:00:00');
    return date.toLocaleDateString('en-US', {
      weekday: 'short',
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const getCurrentTime = () => {
    const now = new Date();
    const hours = String(now.getHours()).padStart(2, '0');
    const minutes = String(now.getMinutes()).padStart(2, '0');
    return `${hours}:${minutes}`;
  };

  const handleClockIn = () => {
    setClockInTime(getCurrentTime());
  };

  const handleClockOut = () => {
    setClockOutTime(getCurrentTime());
  };

  return (
    <div className="app">
      <header className="header">
        <h1>Time Clock</h1>
        <p>Track your work hours and tasks</p>
      </header>

      <main className="main">
        <section className="clock-section">
          <h2>New Shift Entry</h2>

          <div className="form-group">
            <label htmlFor="date">Date</label>
            <input
              type="date"
              id="date"
              value={currentDate}
              onChange={(e) => setCurrentDate(e.target.value)}
            />
          </div>

          <div className="time-inputs">
            <div className="form-group">
              <label htmlFor="clockIn">Clock In</label>
              <input
                type="time"
                id="clockIn"
                value={clockInTime}
                onChange={(e) => setClockInTime(e.target.value)}
              />
            </div>

            <div className="form-group">
              <label htmlFor="clockOut">Clock Out</label>
              <input
                type="time"
                id="clockOut"
                value={clockOutTime}
                onChange={(e) => setClockOutTime(e.target.value)}
              />
            </div>
          </div>

          {clockInTime && clockOutTime && (
            <div className="total-hours">
              Total Hours: <strong>{calculateTotalHours()}</strong>
            </div>
          )}

          <div className="time-blocks-section">
            <div className="section-header">
              <h3>Task Breakdown</h3>
              <button type="button" className="btn-add" onClick={addTimeBlock}>
                + Add Time Block
              </button>
            </div>

            {timeBlocks.length === 0 && (
              <p className="hint">Add time blocks to document what you worked on during your shift</p>
            )}

            {timeBlocks.map((block) => (
              <div key={block.id} className="time-block">
                <div className="time-block-times">
                  <input
                    type="time"
                    placeholder="Start"
                    value={block.startTime}
                    onChange={(e) => updateTimeBlock(block.id, 'startTime', e.target.value)}
                  />
                  <span>to</span>
                  <input
                    type="time"
                    placeholder="End"
                    value={block.endTime}
                    onChange={(e) => updateTimeBlock(block.id, 'endTime', e.target.value)}
                  />
                </div>
                <textarea
                  placeholder="What did you work on? (e.g., Task A, Task B)"
                  value={block.tasks}
                  onChange={(e) => updateTimeBlock(block.id, 'tasks', e.target.value)}
                  rows={2}
                />
                <button
                  type="button"
                  className="btn-remove"
                  onClick={() => removeTimeBlock(block.id)}
                >
                  Remove
                </button>
              </div>
            ))}
          </div>

          <button type="button" className="btn-save" onClick={saveShift}>
            Save Shift
          </button>
        </section>

        <section className="history-section">
          <h2>Shift History</h2>

          {shifts.length === 0 ? (
            <p className="no-shifts">No shifts recorded yet</p>
          ) : (
            <div className="shifts-list">
              {shifts.map((shift) => (
                <div key={shift.id} className="shift-card">
                  <div className="shift-header">
                    <div className="shift-date">{formatDate(shift.date)}</div>
                    <button
                      className="btn-delete"
                      onClick={() => deleteShift(shift.id)}
                    >
                      Delete
                    </button>
                  </div>
                  <div className="shift-times">
                    <span>{formatTime(shift.clockInTime)}</span>
                    <span className="separator">â†’</span>
                    <span>{formatTime(shift.clockOutTime)}</span>
                    <span className="total">{shift.totalHours} hrs</span>
                  </div>
                  {shift.timeBlocks.length > 0 && (
                    <div className="shift-tasks">
                      <h4>Tasks:</h4>
                      {shift.timeBlocks.map((block, index) => (
                        <div key={index} className="task-item">
                          <span className="task-time">
                            {formatTime(block.startTime)} - {formatTime(block.endTime)}
                          </span>
                          <span className="task-desc">{block.tasks}</span>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
    </div>
  );
}

export default App;
