      setEmailData({ newEmail: '', password: '' });
      setTimeout(() => {
        setShowEmailModal(false);
        setEmailSuccess(false);
        // Reload to update user info
        window.location.reload();
      }, 2000);
    } catch (err) {
      setEmailError(err.message || 'Failed to change email');
    } finally {
      setChangingEmail(false);
    }
  };

