                          const formatAction = () => {
                            switch (activity.action) {
                              case 'user_created':
                                return <><strong>{adminName}</strong> added new employee <strong>{targetName}</strong></>;
                              case 'user_updated':
                                return <><strong>{adminName}</strong> updated <strong>{targetName}</strong></>;
                              case 'user_deactivated':
